INSERT INTO procedimientos (codigo, nombre) VALUES ('99219', 'Evaluación y manejo inicial por día de un paciente en observación que requiere estos tres elementos claves: una anamnesis completa; un examen físico completo; una toma de decisión médica de moderada complejidad, se provee consejería y/o coordinación de la atención de salud con otros proveedores u organismos de manera consistente con la naturaleza del problema (s) y las necesidades del paciente y/o la familia, usualmente, el(los) problema(s) de salud que generaron el ingreso a observación son de moderada gravedad');
INSERT INTO procedimientos (codigo, nombre) VALUES ('99220', 'Evaluación y manejo inicial por día de un paciente en observación que requiere estos tres elementos claves: una anamnesis completa; un examen físico completo; una toma de decisión médica de complejidad alta, se provee consejería y/o coordinación de la atención de salud con otros proveedores u organismos de manera consistente con la naturaleza del problema (s) y las necesidades del paciente y/o la familia, usualmente, el(los) problema(s) de salud que generaron el ingreso a observación son de gravedad alta');
